#!/bin/bash

# IMPORTANT: GCP doesnt return sqladmin.googleapis.com/connect, sqladmin.googleapis.com/Databases, compute.googleapis.com/nodeGroupNode, compute.googleapis.com/networkEndpointGroup. 
# NOT INCLUDED IN THE RESOURCE MAPPING JSON FILE AS OF NOW.

set -euo pipefail

# Trap Ctrl+C (SIGINT) and clean up
trap_ctrlc() {
  kill 0
  exit 130
}
trap trap_ctrlc SIGINT SIGTERM

SCRIPT_NAME="GCP Resource Counter for Illumio"

# ------------------------
# Configuration
# ------------------------
VERBOSE=false
SCOPE=""
CONCURRENCY=20

# ------------------------
# Resource Mapping Configuration
# ------------------------
RESOURCE_MAPPING_URL="https://cloudsecure-onboarding-templates.s3.us-west-2.amazonaws.com/cloudsecure/resources.json"
RESOURCE_MAPPING_JSON=""  # Will be set dynamically
MAPPING_VERSION=""
MAPPING_DATE=""
USING_FALLBACK=false

FALLBACK_RESOURCE_MAPPING_BASE64=""

# ------------------------
# Color codes for output
# ------------------------
RED='\033[31m'
GREEN='\033[32m'
YELLOW='\033[33m'
BLUE='\033[36m'
WHITE='\033[37m'
BOLD='\033[1m'
RESET='\033[0m'

# ------------------------
# Output Functions
# ------------------------
log() {
  if $VERBOSE; then
    echo -e "${WHITE}[LOG] $1${RESET}" >&2
  fi
}

log_api_call() {
  if $VERBOSE; then
    echo -e "${WHITE}[API CALL] $1${RESET}" >&2
  fi
}

log_failure() {
  if $VERBOSE; then
    echo -e "${RED}[API FAILURE] $1${RESET}" >&2
  fi
}

error_exit() {
  echo -e "${RED} Error: $1${RESET}" >&2
  echo -e "${YELLOW}  Tip: $2${RESET}" >&2 2>/dev/null || true
  exit 1
}

warn() {
  echo -e "${YELLOW} Warning: $1${RESET}" >&2
}

info() {
  echo -e "${BLUE} $1${RESET}"
}

success() {
  echo -e "${GREEN} $1${RESET}"
}

progress() {
  echo -e "${WHITE} $1${RESET}"
}

# ------------------------
# Resource Mapping Functions
# ------------------------
decode_fallback_mapping() {
  if [[ -z "$FALLBACK_RESOURCE_MAPPING_BASE64" ]]; then
    error_exit "No embedded fallback resource mapping found" \
      "Please ensure the FALLBACK_RESOURCE_MAPPING_BASE64 variable is populated"
  fi
  echo "$FALLBACK_RESOURCE_MAPPING_BASE64" | base64 -d || {
    error_exit "Failed to decode embedded resource mappings" \
      "The embedded fallback may be corrupted"
  }
}

fetch_remote_mapping() {
  local temp_file=$(mktemp)
  local error_file=$(mktemp)
  local success=false
  
  # Try to fetch with curl (primary method)
  if command -v curl >/dev/null 2>&1; then
    log "Fetching resource mappings via curl..."
    if curl -sS --connect-timeout 5 --max-time 10 \
         "$RESOURCE_MAPPING_URL" \
         -o "$temp_file" 2>"$error_file"; then
      if [[ -s "$temp_file" ]] && jq -e '.clouds' "$temp_file" >/dev/null 2>&1; then
        cat "$temp_file"
        success=true
      else
        log "Downloaded file is invalid or empty"
      fi
    else
      log "Curl failed: $(cat "$error_file")"
    fi
  # Fallback to wget if curl not available
  elif command -v wget >/dev/null 2>&1; then
    log "Fetching resource mappings via wget..."
    if wget -q --timeout=10 -O "$temp_file" \
         "$RESOURCE_MAPPING_URL" 2>"$error_file"; then
      if [[ -s "$temp_file" ]] && jq -e '.clouds' "$temp_file" >/dev/null 2>&1; then
        cat "$temp_file"
        success=true
      else
        log "Downloaded file is invalid or empty"
      fi
    else
      log "Wget failed: $(cat "$error_file")"
    fi
  else
    log "Neither curl nor wget available for fetching remote mappings"
  fi
  
  rm -f "$temp_file" "$error_file"
  
  if $success; then
    return 0
  else
    return 1
  fi
}

load_resource_mapping() {
  local temp_mapping_file=$(mktemp)
  
  if fetch_remote_mapping > "$temp_mapping_file" 2>/dev/null; then
    RESOURCE_MAPPING_JSON="$temp_mapping_file"
    USING_FALLBACK=false
    info "Successfully fetched latest resource mappings"
  else
    # Use fallback
    decode_fallback_mapping > "$temp_mapping_file"
    RESOURCE_MAPPING_JSON="$temp_mapping_file"
    USING_FALLBACK=true
    
    if ! $VERBOSE; then
      warn "Using fallback resource mappings (remote source unavailable)"
    fi
  fi
  
  # Validate JSON structure
  if ! jq -e '.clouds.gcp' "$RESOURCE_MAPPING_JSON" >/dev/null 2>&1; then
    error_exit "Invalid JSON structure in resource mappings" \
      "The resource mapping file is corrupted or has an unexpected format"
  fi
  
  # Extract version and date
  MAPPING_VERSION=$(jq -r '.version // "unknown"' "$RESOURCE_MAPPING_JSON" 2>/dev/null)
  MAPPING_DATE=$(jq -r '.updatedDate // "unknown"' "$RESOURCE_MAPPING_JSON" 2>/dev/null)
  
  # Display mapping info
  local mapping_source=$($USING_FALLBACK && echo "embedded" || echo "latest")
  info "Resource mappings loaded (${mapping_source} - v${MAPPING_VERSION}, updated: ${MAPPING_DATE})"
}

cleanup_mapping() {
  if [[ -n "$RESOURCE_MAPPING_JSON" && -f "$RESOURCE_MAPPING_JSON" ]]; then
    rm -f "$RESOURCE_MAPPING_JSON"
  fi
}

# ------------------------
# Cleanup on exit
# ------------------------
cleanup() {
  cleanup_mapping
}
trap cleanup EXIT ERR INT TERM

# ------------------------
# Prerequisites Check
# ------------------------
check_prerequisites() {
  local scope="$1"
  
  # Check required tools
  command -v gcloud >/dev/null 2>&1 || \
    error_exit "gcloud CLI is not installed" \
    "Install it from: https://cloud.google.com/sdk/docs/install"
  
  command -v jq >/dev/null 2>&1 || \
    error_exit "jq is not installed" \
    "Install with: brew install jq (Mac) or apt-get install jq (Linux)"
  
  command -v awk >/dev/null 2>&1 || \
    error_exit "awk is not installed" \
    "Install with: brew install gawk (Mac) or apt-get install gawk (Linux)"
  
  # Check authentication
  if ! gcloud auth list --format="value(account)" 2>/dev/null | grep -q .; then
    error_exit "Not authenticated to GCP" \
    "Run: gcloud auth login"
  fi
  
  # Resource mapping validation is now handled by load_resource_mapping
  
  success "Prerequisites verified"
  
  # Check specific scope access
  if [[ "$scope" =~ ^project:(.+)$ ]]; then
    local project_id="${BASH_REMATCH[1]}"
    info "Verifying access to project: $project_id"
    if ! gcloud projects describe "$project_id" >/dev/null 2>&1; then
      error_exit "Unable to access project: $project_id" \
      "Check if the project exists and you have the required permissions"
    fi
    success "Project access verified"
  elif [[ "$scope" =~ ^folder:(.+)$ ]]; then
    local folder_id="${BASH_REMATCH[1]}"
    info "Verifying access to folder: $folder_id"
    if ! gcloud asset search-all-resources \
      --scope="folders/$folder_id" \
      --asset-types="cloudresourcemanager.googleapis.com/Project" \
      --limit=1 >/dev/null 2>&1; then
      error_exit "Unable to access folder: $folder_id" \
      "Ensure Cloud Asset API is enabled and you have resourcemanager.folders.get permission"
    fi
    success "Folder access verified"
  elif [[ "$scope" =~ ^org:(.+)$ ]]; then
    local org_id="${BASH_REMATCH[1]}"
    info "Verifying access to organization: $org_id"
    if ! gcloud asset search-all-resources \
      --scope="organizations/$org_id" \
      --asset-types="cloudresourcemanager.googleapis.com/Project" \
      --limit=1 >/dev/null 2>&1; then
      error_exit "Unable to access organization: $org_id" \
      "Ensure Cloud Asset API is enabled and you have resourcemanager.organizations.get permission"
    fi
    success "Organization access verified"
  fi
}

# ------------------------
# Initialize Resource Mappings
# ------------------------
initialize_mappings() {
  log "Initializing resource mappings..."
  
  # Build supported asset types
  SUPPORTED_ASSET_TYPES=$(jq -r '.clouds.gcp[] | .script_resource_name' "$RESOURCE_MAPPING_JSON" | tr '\n' ',' | sed 's/,$//')
  
  # Build resource types arrays
  GCP_RESOURCE_TYPES=($(jq -r '.clouds.gcp[] | .script_resource_name' "$RESOURCE_MAPPING_JSON"))
  
  # Build workload ratios
  WORKLOAD_RATIOS_DATA=$(jq -r '.clouds.gcp[] | .resource_type + ":" + (.ratio_iwl | tostring) + ":" + (.ratio_swl | tostring)' "$RESOURCE_MAPPING_JSON" | sort | uniq)
  
  # Build resource mapping
  RESOURCE_MAPPING=($(build_resource_mapping | cut -d' ' -f1))
  
  # Build Illumio categories
  INSIGHTS_RESOURCE_TYPES=()
  while IFS= read -r type; do
    INSIGHTS_RESOURCE_TYPES+=("$type")
  done < <(jq -r '.clouds.gcp[] | select(.enabled_insights == true) | .resource_type' "$RESOURCE_MAPPING_JSON" | sort | uniq)

  SEGMENTATION_RESOURCE_TYPES=()
  while IFS= read -r type; do
    SEGMENTATION_RESOURCE_TYPES+=("$type")
  done < <(jq -r '.clouds.gcp[] | select(.enabled_segmentation == true) | .resource_type' "$RESOURCE_MAPPING_JSON" | sort | uniq)
  
  # Initialize aggregate counts
  INSIGHTS_COUNTS_AGGREGATE=()
  SEGMENTATION_COUNTS_AGGREGATE=()
  for i in "${!INSIGHTS_RESOURCE_TYPES[@]}"; do
    INSIGHTS_COUNTS_AGGREGATE[$i]=0
  done
  for i in "${!SEGMENTATION_RESOURCE_TYPES[@]}"; do
    SEGMENTATION_COUNTS_AGGREGATE[$i]=0
  done
}

# Bash 3 compatible version without associative arrays
build_resource_mapping() {
  local mapping=""
  
  # Build index maps using parallel arrays instead of associative arrays
  local iwl_categories=$(jq -r '.clouds.gcp[] | select(.enabled_insights == true) | .resource_type' "$RESOURCE_MAPPING_JSON" | sort | uniq)
  local swl_categories=$(jq -r '.clouds.gcp[] | select(.enabled_segmentation == true) | .resource_type' "$RESOURCE_MAPPING_JSON" | sort | uniq)
  
  # Store categories in arrays for index lookup
  IWL_CAT_ARRAY=()
  SWL_CAT_ARRAY=()
  
  local i=0
  while IFS= read -r cat; do
    if [[ -n "$cat" ]]; then
      IWL_CAT_ARRAY[$i]="$cat"
      i=$((i+1))
    fi
  done <<< "$iwl_categories"
  
  i=0
  while IFS= read -r cat; do
    if [[ -n "$cat" ]]; then
      SWL_CAT_ARRAY[$i]="$cat"
      i=$((i+1))
    fi
  done <<< "$swl_categories"
  
  # Build mapping array
  local resources=$(jq -r '.clouds.gcp[] | .script_resource_name + "|" + .resource_type' "$RESOURCE_MAPPING_JSON")
  while IFS='|' read -r type cat; do
    local iwl_idx=-1
    local swl_idx=-1
    
    # Find index in IWL categories
    for j in "${!IWL_CAT_ARRAY[@]}"; do
      if [[ "${IWL_CAT_ARRAY[$j]}" == "$cat" ]]; then
        iwl_idx=$j
        break
      fi
    done
    
    # Find index in SWL categories  
    for j in "${!SWL_CAT_ARRAY[@]}"; do
      if [[ "${SWL_CAT_ARRAY[$j]}" == "$cat" ]]; then
        swl_idx=$j
        break
      fi
    done
    
    mapping="$mapping$type|$iwl_idx|$swl_idx\n"
  done <<< "$resources"
  
  echo -e "$mapping"
}

# ------------------------
# Core Functions
# ------------------------
get_workload_ratio() {
  local resource_type="$1"
  local ratio_type="$2"
  local ratio_line=$(grep -E "^${resource_type}:" <<< "$WORKLOAD_RATIOS_DATA" || echo "Other:0.01:0.01")
  if [[ "$ratio_type" == "iw" ]]; then
    cut -d':' -f2 <<< "$ratio_line"
  else
    cut -d':' -f3 <<< "$ratio_line"
  fi
}

# Cross-platform file locking
lock_file() {
  local file="$1"
  local timeout="${2:-30}"
  local lock_file="${file}.lock"
  local count=0
  
  while [ $count -lt $timeout ]; do
    if mkdir "$lock_file" 2>/dev/null; then
      return 0
    fi
    sleep 0.1
    count=$((count + 1))
  done
  return 1
}

unlock_file() {
  local file="$1"
  rmdir "${file}.lock" 2>/dev/null || true
}

resolve_scope_to_projects() {
  local scope="$1"
  local ids
  
  if [[ "$scope" =~ ^project:(.+)$ ]]; then
    echo "${BASH_REMATCH[1]}"
  elif [[ "$scope" =~ ^folder:(.+)$ ]]; then
    log "Fetching projects from folder ${BASH_REMATCH[1]}"
    log_api_call "Executing: gcloud asset search-all-resources --scope=folders/${BASH_REMATCH[1]} --asset-types=cloudresourcemanager.googleapis.com/Project"
    ids=$(gcloud asset search-all-resources \
      --scope="folders/${BASH_REMATCH[1]}" \
      --asset-types="cloudresourcemanager.googleapis.com/Project" \
      --format="value(name)" 2>/dev/null | sed 's|.*/projects/||' | grep -E '^[a-z0-9][a-z0-9-]*[a-z0-9]$')
    
    if [[ -z "$ids" ]]; then
      log_failure "No projects found in folder ${BASH_REMATCH[1]}"
      error_exit "No projects found in folder ${BASH_REMATCH[1]}" \
        "Ensure the folder contains projects and you have the necessary permissions"
    fi
    echo "$ids" | tr '\n' ' '
  elif [[ "$scope" =~ ^org:(.+)$ ]]; then
    log "Fetching projects from organization ${BASH_REMATCH[1]}"
    log_api_call "Executing: gcloud asset search-all-resources --scope=organizations/${BASH_REMATCH[1]} --asset-types=cloudresourcemanager.googleapis.com/Project"
    ids=$(gcloud asset search-all-resources \
      --scope="organizations/${BASH_REMATCH[1]}" \
      --asset-types="cloudresourcemanager.googleapis.com/Project" \
      --format="value(name)" 2>/dev/null | sed 's|.*/projects/||' | grep -E '^[a-z0-9][a-z0-9-]*[a-z0-9]$')
    
    if [[ -z "$ids" ]]; then
      log_failure "No projects found in organization ${BASH_REMATCH[1]}"
      error_exit "No projects found in organization ${BASH_REMATCH[1]}" \
        "Ensure the organization contains projects and you have the necessary permissions"
    fi
    echo "$ids" | tr '\n' ' '
  else
    error_exit "Invalid scope format: $scope" \
      "Use project:<ID>, folder:<ID>, or org:<ID>"
  fi
}

count_resources_for_project() {
  local project="$1"
  local temp_file="$2"
  
  local insights_counts=()
  local segmentation_counts=()
  for i in "${!INSIGHTS_RESOURCE_TYPES[@]}"; do insights_counts[$i]=0; done
  for i in "${!SEGMENTATION_RESOURCE_TYPES[@]}"; do segmentation_counts[$i]=0; done
  
  log "Counting resources in project: $project"
  
  local tmp=$(mktemp)
  local error_output=$(mktemp)
  
  log_api_call "Executing: gcloud asset list --project=$project --asset-types=$SUPPORTED_ASSET_TYPES"
  if ! gcloud asset list \
      --project="$project" \
      --asset-types="$SUPPORTED_ASSET_TYPES" \
      --content-type=resource \
      --format="csv(assetType)" > "$tmp" 2> "$error_output"; then
    
    local error_msg=$(cat "$error_output")
    log_failure "Failed to list assets in project $project: $error_msg"
    if [[ "$error_msg" =~ "Cloud Asset API has not been used" ]] || [[ "$error_msg" =~ "PERMISSION_DENIED" ]]; then
      warn "Skipping project $project: Cloud Asset API not enabled or insufficient permissions"
    else
      warn "Failed to list assets in project $project"
    fi
    
    # Write zeros for this project
    if lock_file "$temp_file"; then
      echo -n "$project" >> "$temp_file"
      for i in "${!INSIGHTS_RESOURCE_TYPES[@]}"; do echo -n ":0" >> "$temp_file"; done
      for i in "${!SEGMENTATION_RESOURCE_TYPES[@]}"; do echo -n ":0" >> "$temp_file"; done
      echo >> "$temp_file"
      unlock_file "$temp_file"
    fi
    
    rm -f "$tmp" "$error_output"
    return
  fi
  
  rm -f "$error_output"
  
  # Process assets
  local line_count=$(wc -l < "$tmp")
  if [[ $line_count -gt 1 ]]; then
    while IFS=',' read -r asset_type rest; do
      asset_type=${asset_type//\"/}
      
      # Check if this specific resource has insights enabled
      local has_insights=$(jq -r --arg type "$asset_type" \
        '.clouds.gcp[] | select(.script_resource_name == $type and .enabled_insights == true) | .resource_type' \
        "$RESOURCE_MAPPING_JSON" | head -1)
      
      # Check if this specific resource has segmentation enabled  
      local has_segmentation=$(jq -r --arg type "$asset_type" \
        '.clouds.gcp[] | select(.script_resource_name == $type and .enabled_segmentation == true) | .resource_type' \
        "$RESOURCE_MAPPING_JSON" | head -1)
      
      if [[ -n "$has_insights" ]]; then
        # Find the category index and increment
        for i in "${!INSIGHTS_RESOURCE_TYPES[@]}"; do
          if [[ "${INSIGHTS_RESOURCE_TYPES[$i]}" == "$has_insights" ]]; then
            insights_counts[$i]=$((insights_counts[$i]+1))
            break
          fi
        done
      fi
      
      if [[ -n "$has_segmentation" ]]; then
        # Find the category index and increment
        for i in "${!SEGMENTATION_RESOURCE_TYPES[@]}"; do
          if [[ "${SEGMENTATION_RESOURCE_TYPES[$i]}" == "$has_segmentation" ]]; then
            segmentation_counts[$i]=$((segmentation_counts[$i]+1))
            break
          fi
        done
      fi
    done < <(tail -n +2 "$tmp")
  fi
  
  rm -f "$tmp"
  
  # Write results
  if lock_file "$temp_file"; then
    echo -n "$project" >> "$temp_file"
    for i in "${!INSIGHTS_RESOURCE_TYPES[@]}"; do
      echo -n ":${insights_counts[$i]}" >> "$temp_file"
    done
    for i in "${!SEGMENTATION_RESOURCE_TYPES[@]}"; do
      echo -n ":${segmentation_counts[$i]}" >> "$temp_file"
    done
    echo >> "$temp_file"
    unlock_file "$temp_file"
  fi
  
  success "Finished counting resources for $project"
}

count_resources_concurrently() {
  local projects_str="$1"
  IFS=' ' read -r -a projects <<< "$projects_str"
  
  local dir=$(mktemp -d)
  local out="$dir/counts.txt"
  local pids=()
  
  info "Scanning ${#projects[@]} project(s)..."
  
  for p in "${projects[@]}"; do
    if [[ -n "$p" && "$p" =~ ^[a-z0-9][a-z0-9-]*[a-z0-9]$ ]]; then
      # Manage concurrency
      while [ "${#pids[@]}" -ge "$CONCURRENCY" ]; do
        local new_pids=()
        for pid in "${pids[@]}"; do
          if kill -0 "$pid" 2>/dev/null; then
            new_pids[${#new_pids[@]}]="$pid"
          fi
        done
        pids=("${new_pids[@]}")
        sleep 0.1
      done
      
      count_resources_for_project "$p" "$out" &
      pids[${#pids[@]}]="$!"
    fi
  done
  
  # Wait for all jobs
  for pid in "${pids[@]}"; do 
    wait "$pid" 2>/dev/null || true
  done
  
  # Clean up locks
  rm -rf "$out".lock
  
  # Aggregate results
  if [[ -f "$out" ]]; then
    while IFS=':' read -r proj rest; do
      if [[ -n "$proj" && -n "$rest" ]]; then
        IFS=':' read -r -a vals <<< "$rest"
        local val_index=0
        
        for i in "${!INSIGHTS_RESOURCE_TYPES[@]}"; do
          if [[ $val_index -lt ${#vals[@]} && ${vals[$val_index]} =~ ^[0-9]+$ ]]; then
            INSIGHTS_COUNTS_AGGREGATE[$i]=$((INSIGHTS_COUNTS_AGGREGATE[$i] + vals[val_index]))
          fi
          val_index=$((val_index + 1))
        done
        
        for i in "${!SEGMENTATION_RESOURCE_TYPES[@]}"; do
          if [[ $val_index -lt ${#vals[@]} && ${vals[$val_index]} =~ ^[0-9]+$ ]]; then
            SEGMENTATION_COUNTS_AGGREGATE[$i]=$((SEGMENTATION_COUNTS_AGGREGATE[$i] + vals[val_index]))
          fi
          val_index=$((val_index + 1))
        done
      fi
    done < "$out"
  fi
  
  rm -rf "$dir"
}

calculate_workloads() {
  local total_iw=0 total_sw=0
  
  for i in "${!INSIGHTS_RESOURCE_TYPES[@]}"; do
    local ct=${INSIGHTS_COUNTS_AGGREGATE[$i]}
    local rt=${INSIGHTS_RESOURCE_TYPES[$i]}
    local iw=$(get_workload_ratio "$rt" iw)
    # Use awk for comparison and calculation
    if [ $(echo "$iw" | awk '{if ($1 > 0) print 1; else print 0}') -eq 1 ]; then
      total_iw=$(echo "$total_iw $ct $iw" | awk '{print $1 + $2 * $3}')
    fi
  done
  
  for i in "${!SEGMENTATION_RESOURCE_TYPES[@]}"; do
    local ct=${SEGMENTATION_COUNTS_AGGREGATE[$i]}
    local rt=${SEGMENTATION_RESOURCE_TYPES[$i]}
    local sw=$(get_workload_ratio "$rt" sw)
    # Use awk for comparison and calculation
    if [ $(echo "$sw" | awk '{if ($1 > 0) print 1; else print 0}') -eq 1 ]; then
      total_sw=$(echo "$total_sw $ct $sw" | awk '{print $1 + $2 * $3}')
    fi
  done
  
  # Round up using awk
  total_iw=$(echo "$total_iw" | awk '{print int($1+0.999)}')
  total_sw=$(echo "$total_sw" | awk '{print int($1+0.999)}')
  echo "$total_iw:$total_sw"
}

print_summary() {
  local scope="$1"
  local label=""
  
  # Format label based on scope
  if [[ "$scope" =~ ^org:(.+)$ ]]; then
    label="GCP Organization: ${BASH_REMATCH[1]}"
  elif [[ "$scope" =~ ^folder:(.+)$ ]]; then
    label="GCP Folder: ${BASH_REMATCH[1]}"
  elif [[ "$scope" =~ ^project:(.+)$ ]]; then
    label="GCP Project: ${BASH_REMATCH[1]}"
  fi
  
  echo
  echo ""
  echo -e "${BOLD}  Resource Summary for $label${RESET}"
  echo ""
  echo
  
  # Insights Workloads
  echo -e "${BOLD}${BLUE}  Illumio Insights Workloads (IWL)${RESET}"
  echo ""
  printf "${BOLD}%-40s %-10s %-10s${RESET}\n" "Resource Type" "Count" "IWL"
  echo ""
  
  local total_insights_resources=0
  for i in "${!INSIGHTS_RESOURCE_TYPES[@]}"; do
    local rt=${INSIGHTS_RESOURCE_TYPES[$i]}
    local count=${INSIGHTS_COUNTS_AGGREGATE[$i]}
    local ratio=$(get_workload_ratio "$rt" iw)
    local workload=0
    
    total_insights_resources=$((total_insights_resources + count))
    
    ratio=$(echo "$ratio" | tr -d '[:space:]')
    if [[ -n "$ratio" && "$ratio" =~ ^[0-9]+(\.[0-9]+)?$ ]]; then
      # Use awk for comparison and calculation
      if [ $(echo "$ratio" | awk '{if ($1 > 0) print 1; else print 0}') -eq 1 ]; then
        workload=$(echo "$count $ratio" | awk '{printf "%.2f", $1 * $2}')
      fi
    fi
    
    if [[ $count -gt 0 ]]; then
      printf "%-40s %-10s %-10s\n" "$rt" "$count" "$workload"
    fi
  done
  
  local w=$(calculate_workloads)
  IFS=':' read -r total_iw total_sw <<< "$w"
  
  echo ""
  printf "${BOLD}%-40s %-10s ${GREEN}%-10s${RESET}\n" "TOTAL" "$total_insights_resources" "$total_iw IWLs"
  echo
  
  # Segmentation Workloads
  echo -e "${BOLD}${BLUE}  Illumio Segmentation Workloads (SWL)${RESET}"
  echo ""
  printf "${BOLD}%-40s %-10s %-10s${RESET}\n" "Resource Type" "Count" "SWL"
  echo ""
  
  local total_segmentation_resources=0
  for i in "${!SEGMENTATION_RESOURCE_TYPES[@]}"; do
    local rt=${SEGMENTATION_RESOURCE_TYPES[$i]}
    local count=${SEGMENTATION_COUNTS_AGGREGATE[$i]}
    local ratio=$(get_workload_ratio "$rt" sw)
    local workload=0
    
    total_segmentation_resources=$((total_segmentation_resources + count))
    
    ratio=$(echo "$ratio" | tr -d '[:space:]')
    if [[ -n "$ratio" && "$ratio" =~ ^[0-9]+(\.[0-9]+)?$ ]]; then
      # Use awk for comparison and calculation
      if [ $(echo "$ratio" | awk '{if ($1 > 0) print 1; else print 0}') -eq 1 ]; then
        workload=$(echo "$count $ratio" | awk '{printf "%.2f", $1 * $2}')
      fi
    fi
    
    if [[ $count -gt 0 ]]; then
      printf "%-40s %-10s %-10s\n" "$rt" "$count" "$workload"
    fi
  done
  
  echo ""
  printf "${BOLD}%-40s %-10s ${GREEN}%-10s${RESET}\n" "TOTAL" "$total_segmentation_resources" "$total_sw SWLs"
}

print_help() {
  echo -e "${BOLD}$SCRIPT_NAME${RESET}

${BOLD}USAGE:${RESET}
  $0 --scope <scope> [options]

${BOLD}REQUIRED:${RESET}
  --scope <scope>    Specify the GCP scope to scan:
                      project:<PROJECT_ID>  - Single project
                      folder:<FOLDER_ID>    - All projects in folder
                      org:<ORG_ID>          - All projects in organization

${BOLD}OPTIONS:${RESET}
  --verbose          Enable detailed logging
  --help             Show this help message

${BOLD}EXAMPLES:${RESET}
  # Scan a single project
  $0 --scope project:my-project-123

  # Scan all projects in a folder
  $0 --scope folder:123456789

  # Scan entire organization with verbose output
  $0 --scope org:123456789012 --verbose

${BOLD}PREREQUISITES:${RESET}
   gcloud CLI installed and authenticated
   jq and awk utilities installed
   Cloud Asset API enabled
   Appropriate IAM permissions

${BOLD}REQUIRED PERMISSIONS:${RESET}
   cloudasset.assets.listResource
   resourcemanager.projects.get
   resourcemanager.folders.get (for folder scope)
   resourcemanager.organizations.get (for org scope)"
}

# ------------------------
# Main Function
# ------------------------
main() {
  local scope=""
  local help=false
  local verbose=false
  
  # Parse arguments
  while [[ $# -gt 0 ]]; do
    case $1 in
      --scope)
        scope="$2"
        shift 2
        ;;
      --verbose)
        verbose=true
        shift
        ;;
      --help)
        help=true
        shift
        ;;
      *)
        error_exit "Unknown option: $1" \
        "Run '$0 --help' for usage information"
        ;;
    esac
  done
  
  if $help; then
    print_help
    exit 0
  fi
  
  VERBOSE=$verbose
  
  if [[ -z "$scope" ]]; then
    error_exit "Scope is required" \
    "Use --scope project:<ID>, folder:<ID>, or org:<ID>"
  fi
  
  # Validate scope format
  if ! [[ "$scope" =~ ^(project|folder|org):(.+)$ ]]; then
    error_exit "Invalid scope format: $scope" \
    "Use format: project:<ID>, folder:<ID>, or org:<ID>"
  fi
  
  info "Starting $SCRIPT_NAME"
  
  # Check prerequisites
  check_prerequisites "$scope"
  
  # Load resource mappings
  load_resource_mapping
  
  # Initialize mappings
  initialize_mappings
  
  # Resolve scope to projects
  progress "Resolving scope to projects..."
  local projects=$(resolve_scope_to_projects "$scope")
  if [[ -z "$projects" ]]; then
    error_exit "No accessible projects found in scope: $scope" \
    "Verify the scope exists and you have the necessary permissions"
  fi
  
  # Convert to array for counting
  IFS=' ' read -r -a projects_array <<< "$projects"
  info "Found ${#projects_array[@]} project(s) to scan"
  
  # Count resources
  progress "Scanning resources (this may take a few minutes)..."
  count_resources_concurrently "$projects"
  
  # Print summary
  print_summary "$scope"
}

# Execute main function
main "$@"